INSERT INTO `wp_icl_strings` VALUES (1057,'en','default','60be57d14942b01e4d2bccff314615dc','Pages are similar to posts in that they have a title, body text, and associated metadata, but they are different in that they are not part of the chronological blog stream, kind of like permanent posts. Pages are not categorized or tagged, but can have a hierarchy. You can nest pages under other pages by making one the &#8220;Parent&#8221; of the other, creating a group of pages.',NULL,NULL,'LINE',NULL,0,'','5bb48b4ca8537da5fe1c709d252a3dab','',NULL);
