INSERT INTO `wp_icl_strings` VALUES (3219,'en','default','ce99e8b4ddb1935ad216af170d465663','If you would like to see more plugins to choose from, click on the &#8220;Add New&#8221; button and you will be able to browse or search for additional plugins from the <a href=\"%s\">WordPress Plugin Directory</a>. Plugins in the WordPress Plugin Directory are designed and developed by third parties, and are compatible with the license WordPress uses. Oh, and they&#8217;re free!',NULL,NULL,'LINE',NULL,0,'','918021f3c1c7333bbb985975acdf69b3','',NULL);
