INSERT INTO `wp_icl_strings` VALUES (3222,'en','default','fcd99583341d36fed9a308df418b0501','Most of the time, plugins play nicely with the core of WordPress and with other plugins. Sometimes, though, a plugin&#8217;s code will get in the way of another plugin, causing compatibility issues. If your site starts doing strange things, this may be the problem. Try deactivating all your plugins and re-activating them in various combinations until you isolate which one(s) caused the issue.',NULL,NULL,'LINE',NULL,0,'','f0345a79c1b5429286734217a80c98ed','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3223,'en','default','37a24e605a3cda57698e531f125740bc','If something goes wrong with a plugin and you can&#8217;t use WordPress, delete or rename that file in the %s directory and it will be automatically deactivated.',NULL,NULL,'LINE',NULL,0,'','b9b8f473d110eff046e9836bde623ed6','',NULL);
