INSERT INTO `wp_icl_strings` VALUES (3265,'en','default','6ce32e02fc5aad094d1708046e3ddc76','Plugins hook into WordPress to extend its functionality with custom features. Plugins are developed independently from the core WordPress application by thousands of developers all over the world. All plugins in the official <a href=\"%s\">WordPress Plugin Directory</a> are compatible with the license WordPress uses.',NULL,NULL,'LINE',NULL,0,'','9f0c80252eb8b23b4d92b0ac43489e45','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3266,'en','default','2a4f1cc2e99d292a9132ee9cfb9c3b26','You can find new plugins to install by searching or browsing the directory right here in your own Plugins section.',NULL,NULL,'LINE',NULL,0,'','f21a09c0e4a3b3eee841ef6b410151fa','',NULL);
