INSERT INTO `wp_icl_strings` VALUES (3268,'en','default','a379ef32870b64a6ef175d6e3da0bf17','If you know what you&#8217;re looking for, Search is your best bet. The Search screen has options to search the WordPress Plugin Directory for a particular Term, Author, or Tag. You can also search the directory by selecting popular tags. Tags in larger type mean more plugins have been labeled with that tag.',NULL,NULL,'LINE',NULL,0,'','2dfd3ebb5bbf8256c5c0cc8fefb8b3c8','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3269,'en','default','2448f34ad2e38271ffbcf4d299c17c5d','If you just want to get an idea of what&#8217;s available, you can browse Featured and Popular plugins by using the links above the plugins list. These sections rotate regularly.',NULL,NULL,'LINE',NULL,0,'','575aca988849707fdbbfd60be15902e1','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3270,'en','default','b816582da0fe96d33555b82e7cf35504','You can also browse a user&#8217;s favorite plugins, by using the Favorites link above the plugins list and entering their WordPress.org username.',NULL,NULL,'LINE',NULL,0,'','96e13d80dd4d210f6e818be399382890','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3271,'en','default','be3d2742b2dbbb1878b22b8702b6e8d7','If you want to install a plugin that you&#8217;ve downloaded elsewhere, click the Upload Plugin button above the plugins list. You will be prompted to upload the .zip package, and once uploaded, you can activate the new plugin.',NULL,NULL,'LINE',NULL,0,'','313943fe8cc1a3d691aa966a6a6819c7','',NULL);
