INSERT INTO `wp_icl_strings` VALUES (3410,'en','default','7bb5b05b47216f59eeab4b23d0014277','Welcome to your WordPress Dashboard! This is the screen you will see when you log in to your site, and gives you access to all the site management features of WordPress. You can get help for any screen by clicking the Help tab above the screen title.',NULL,NULL,'LINE',NULL,0,'','caa3ada644015d7da497c61e78d64e63','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3411,'en','default','030eb735ca4d9aed22505e6adb6e37c1','The left-hand navigation menu provides links to all of the WordPress administration screens, with submenu items displayed on hover. You can minimize this menu to a narrow icon strip by clicking on the Collapse Menu arrow at the bottom.',NULL,NULL,'LINE',NULL,0,'','a199554e617c102a6467d92c120ed216','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3412,'en','default','ca887a8513c595e757361973c401dfc3','Links in the Toolbar at the top of the screen connect your dashboard and the front end of your site, and provide access to your profile and helpful WordPress information.',NULL,NULL,'LINE',NULL,0,'','9ae02a33eb223e22333569fc7a0b76e7','',NULL);
