INSERT INTO `wp_icl_strings` VALUES (3414,'en','default','5d3cfb1969cd32ce072f4a154329dbf9','You can use the following controls to arrange your Dashboard screen to suit your workflow. This is true on most other administration screens as well.',NULL,NULL,'LINE',NULL,0,'','96659bd2478b2ec608973ec63c74de4c','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3415,'en','default','a98be58ec342822c43f9a60f423b1d2f','<strong>Screen Options</strong> &mdash; Use the Screen Options tab to choose which Dashboard boxes to show.',NULL,NULL,'LINE',NULL,0,'','ed407f0af001c6226b4cc9be2875d701','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3416,'en','default','71ce1121b4c5579d8f2c8e71335b9a22','<strong>Drag and Drop</strong> &mdash; To rearrange the boxes, drag and drop by clicking on the title bar of the selected box and releasing when you see a gray dotted-line rectangle appear in the location you want to place the box.',NULL,NULL,'LINE',NULL,0,'','5152a8dbc58f8c3531ec3b414be417aa','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3417,'en','default','b65fa8d75f92ea10c226c9c9e107cfc6','<strong>Box Controls</strong> &mdash; Click the title bar of the box to expand or collapse it. Some boxes added by plugins may have configurable content, and will show a &#8220;Configure&#8221; link in the title bar if you hover over it.',NULL,NULL,'LINE',NULL,0,'','54545fe38eda999a7a67927af2e0e559','',NULL);
