INSERT INTO `wp_icl_strings` VALUES (3420,'en','default','ecdb52fb00d5aabe4e9d24b436f9b9fc','<strong>Activity</strong> &mdash; Shows the upcoming scheduled posts, recently published posts, and the most recent comments on your posts and allows you to moderate them.',NULL,NULL,'LINE',NULL,0,'','92492a427dfb72cd1b97719e8e35e896','',NULL);
INSERT INTO `wp_icl_strings` VALUES (3421,'en','default','78bf51b73e62500109ff97739c24bb7d','<strong>Quick Draft</strong> &mdash; Allows you to create a new post and save it as a draft. Also displays links to the 5 most recent draft posts you\'ve started.',NULL,NULL,'LINE',NULL,0,'','ccbc678b1234e3865ff243cf2f5e033b','',NULL);
