INSERT INTO `wp_icl_strings` VALUES (4574,'en','default','253a7846f846822cef57c24c1e7773a1','Menus can be displayed in locations defined by your theme, even used in sidebars by adding a &#8220;Navigation Menu&#8221; widget on the <a href=\"%1$s\">Widgets</a> screen. If your theme does not support the navigation menus feature (the default themes, %2$s and %3$s, do), you can learn about adding this support by following the Documentation link to the side.',NULL,NULL,'LINE',NULL,0,'','b928a19f854171bb78ebcc1581a3ebbd','',NULL);
