INSERT INTO `wp_icl_strings` VALUES (4580,'en','default','72caeed1ff140d706e86977a0bf7d12c','You can assign theme locations to individual menus by <strong>selecting the desired settings</strong> at the bottom of the menu editor. To assign menus to all theme locations at once, <strong>visit the Manage Locations tab</strong> at the top of the screen.',NULL,NULL,'LINE',NULL,0,'','9362d167fdacec13de259ffccbd99596','',NULL);
