INSERT INTO `wp_icl_strings` VALUES (4582,'en','default','5b62ce98d137cebbf42db38a8e516f3a','Each navigation menu may contain a mix of links to pages, categories, custom URLs or other content types. Menu links are added by selecting items from the expanding boxes in the left-hand column below.',NULL,NULL,'LINE',NULL,0,'','199285e9c5308dac57facee28ba6853e','',NULL);
INSERT INTO `wp_icl_strings` VALUES (4583,'en','default','2bc2b92ef21e54b7d57719af3e03b906','<strong>Clicking the arrow to the right of any menu item</strong> in the editor will reveal a standard group of settings. Additional settings such as link target, CSS classes, link relationships, and link descriptions can be enabled and disabled via the Screen Options tab.',NULL,NULL,'LINE',NULL,0,'','44adfce5db156b9d67e7c275453a89b9','',NULL);
