INSERT INTO `wp_posts` VALUES (128,1,'2018-12-27 18:17:20','2018-12-27 18:17:20','<p>Toveri on suomalainen vapaan koodin kulunvalvontalaite. Laitetta toimittaa kirjastoalan järjestelmäasiantuntijat. Toveri on valmiiksi yhteensopiva Koha-kirjastojärjestelmän kanssa.</p>\n\n<!-- wp:paragraph -->\n<p><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:media-text {\"align\":\"\",\"mediaId\":121,\"mediaType\":\"image\",\"isStackedOnMobile\":true} -->\n<div class=\"wp-block-media-text is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://homepage/wp-content/uploads/2018/12/Eno-768x1024.jpg\" alt=\"Toveri Enon kirjastossa\" class=\"wp-image-121\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><em>Kuvassa Toveri Enon kirjastossa.</em><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Toveri on Suomen ainoa vapaan koodin kulunvalvontalaite.</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Toveri on suunniteltu kestämään suomalaiset ääriolosuhteet, kesähelteistä talven paukkupakkasiin.</h3>\n<!-- /wp:heading --></div></div>\n<!-- /wp:media-text -->\n\n<article class=\"elementor-element elementor-element-99f31fd elementor-section-boxed elementor-section-height-default elementor-section-height-default elementor-section elementor-top-section\" data-id=\"99f31fd\" data-element_type=\"section\">\n<div class=\"elementor-container elementor-column-gap-wider\">\n<div class=\"elementor-row\">\n<div class=\"elementor-element elementor-element-806ae3e elementor-column elementor-col-100 elementor-top-column\" data-id=\"806ae3e\" data-element_type=\"column\">\n<div class=\"elementor-column-wrap elementor-element-populated\">\n<div class=\"elementor-widget-wrap\">\n<div class=\"elementor-element elementor-element-ed3abeb elementor-widget elementor-widget-text-editor\" data-id=\"ed3abeb\" data-element_type=\"text-editor.default\">\n<d