INSERT INTO `wp_posts` VALUES (386,1,'2019-02-17 17:55:48','2019-02-17 17:55:48','<!-- wp:media-text {\"align\":\"\",\"mediaId\":261,\"mediaType\":\"image\",\"mediaWidth\":29,\"isStackedOnMobile\":true} -->\r\n<div class=\"wp-block-media-text is-stacked-on-mobile\" style=\"grid-template-columns: 29% auto;\">\r\n<figure class=\"wp-block-media-text__media\"><img class=\"wp-image-261\" src=\"http://homepage/wp-content/uploads/2019/02/Koha-logo-1024x294.png\" alt=\"Koha logo\" /></figure>\r\n<div class=\"wp-block-media-text__content\"><!-- wp:html -->\r\n<p style=\"margin-top: 50px; font-size: 38px; text-align: center;\"><strong><em>Pienille kirjastoille maailman paras kirjastojärjestelmäratkaisu.</em></strong></p>\r\n<!-- /wp:html -->\r\n\r\n</div>\r\n</div>\r\n<!-- /wp:media-text -->\r\n\r\n<!-- wp:separator -->\r\n\r\n<hr class=\"wp-block-separator\" />\r\n\r\n<!-- /wp:separator -->\r\n\r\n<!-- wp:spacer {\"height\":20} -->\r\n<div class=\"wp-block-spacer\" style=\"height: 20px;\" aria-hidden=\"true\"></div>\r\n<!-- /wp:spacer -->\r\n\r\n<!-- wp:columns -->\r\n<div class=\"wp-block-columns has-2-columns\"><!-- wp:column -->\r\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"fontSize\":\"large\"} -->\r\n<p class=\"has-large-font-size\"><strong>Oikeasti avoin kirjastojärjestelmä</strong></p>\r\n<!-- /wp:paragraph -->\r\n\r\n</div>\r\n<!-- /wp:column -->\r\n\r\n<!-- wp:column -->\r\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"fontSize\":\"medium\"} -->\r\n<p class=\"has-medium-font-size\">Koha on vapaan ja avoimen lähdekoodin lisenssillä toteutettu kirjastojärjestelmä, jonka taustalla on laaja kansainvälinen käyttäjä- ja kehittäjäverkosto. Vapaan lähdekoodin periaatteiden mukaisesti Kohan toiminta ja kehitys on läpinäkyvää, jolloin julkisilla varoilla hankittu palvelu on julkisesti kaikkien käytettävissä ja tarkasteltavissa.</p>\r\n<!-- /wp:paragraph -->\r\n\r\n</div>\r\n<!-- /wp:column -->\r\n\r\n</div>\r\n<!-- /wp:columns -->\r\n\r\n<!-- wp:spacer {\"height\":20} -->\r\n<div class=\"wp-block-spacer\" style=\"height: 20px;\" aria-hidden=\"true\"></div>\r\n<!-- /wp:spacer -->\r\n\r\n<!-- wp:columns -->\r\n<div class=\"wp-block-columns has-2-columns\"><!-- wp:column -->\r\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"fontSize\":\"large\"} -->\r\n<p class=\"has-large-font-size\"><strong>Mukautuu tarpeisiinne</strong></p>\r\n<!-- /wp:paragraph -->\r\n\r\n</div>\r\n<!-- /wp:column -->\r\n\r\n<!-- wp:column -->\r\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"fontSize\":\"medium\"} -->\r\n<p class=\"has-medium-font-size\">Kohan monipuoliset ominaisuudet takaavat järjestelmän soveltuvuuden eri tarpeisiin. Kohaan sisältyvät käytännössä kaikki toiminallisuudet, joita kirjasto tarvitsee. Koha sisältää ja tukee eri rajapintoja, jonka vuoksi se integroituu vaivattomasti muiden järjestelmien ja laitteiden kanssa. Koha on yhteensopiva usean suomalaisen palvelun, kuten Finnan ja Melindan kanssa.</p>\r\n<!-- /wp:paragraph -->\r\n\r\n</div>\r\n<!-- /wp:column -->\r\n\r\n</div>\r\n<!-- /wp:columns -->\r\n\r\n<!-- wp:spacer {\"height\":20} -->\r\n<div class=\"wp-block-spacer\" style=\"height: 20px;\" aria-hidden=\"true\"></div>\r\n<!-- /wp:spacer -->\r\n\r\n<!-- wp:columns -->\r\n<div class=\"wp-block-columns has-2-columns\"><!-- wp:column -->\r\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"fontSize\":\"large\"} -->\r\n<p class=\"has-large-font-size\"><strong>Vankalla kokemuksella</strong></p>\r\n<!-- /wp:paragraph -->\r\n\r\n</div>\r\n<!-- /wp:column -->\r\n\r\n<!-- wp:column -->\r\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"fontSize\":\"medium\"} -->\r\n<p class=\"has-medium-font-size\">Ammattilaisillamme on yhteensä yli kymmenen vuoden Koha-kokemus. Osallistumme aktiivisesti kansainvälisiin Koha-tapaamisiin, -koulutuksiin sekä muihin vapaan ja avoimen lähdekoodin tapahtumiin. Toimintatapamme on ideologinen valinta, sillä avoimuus, läpinäkyvyys ja rehellisyys ovat meille tärkeitä arvoja. Kohan kautta pääse